<?php

namespace Kub\UserBundle\Entity;

use Doctrine\ORM\EntityRepository;

/**
 * EleveRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class EleveRepository extends UserRepository
{
	// fonction utilisee pour charger l'ensemble des donnÃ©es de l'utilisateur
	public function findOneByUsername($username)
	{
		$qb = $this->createQueryBuilder("e")
			->leftJoin('e.groupes', 'g')
			->addSelect('g')
			->leftJoin('e.niveau', 'n')
			->addSelect('n')
			->leftJoin('e.tuteurs', 't')
			->addSelect('t')
			->where("e.username = :username")
			->setParameter("username", $username)
		;

		return $qb->getQuery()->getSingleResult();
	}

	public function findByNiveauName($niveau)
	{
		$qb = $this->createQueryBuilder("e")
			->join('e.niveau', 'n')
			->where("n.name = :niveau")
			->setParameter("niveau", $niveau)
		;

		return $qb->getQuery()->getResult();
	}

	public function getEDTByid($id)
	{
		$qb = $this->createQueryBuilder("e")
			->join('e.groupes', 'g')
			->addSelect('g')
			->join('g.cours', 'c')
			->addSelect('c')
			->join('c.horaires', 'h')
			->addSelect('h')
			->join('h.jour', 'j')
			->addSelect('j')
			->where('e.id = :id')
			->setParameter('id', $id)
			->orderBy('j.id, h.debut')
		;

		return $qb->getQuery()->getSingleResult();
	}
}
